function populateData(){$.ajax({type:"GET",url:"/data",success:function(a){a.map(function(a){switch(a.key){case"adjectives":adjectives=a.values;break;case"modifying_adjectives":modedAdj=a.values;break;case"topics":topicsCovered=a.values}})}})}function randomNum(a,b){return Math.round(Math.random()*(b-a)+a)}function generatePhrase(){var a=adjectives[randomNum(0,adjectives.length-1)],b=modedAdj[randomNum(0,modedAdj.length-1)],c=topicsCovered[randomNum(0,topicsCovered.length-1)];return b+" "+a+" "+c}var adjectives=[],modedAdj=[],topicsCovered=[];$(document).ready(function(){populateData(),$("#generatePhrase").on("click",function(){$(".phrase").remove();var a="<h2 class='phrase'>"+generatePhrase()+"</h2>";$(".generatedPhrase").append(a)})});